import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu, Input, Row, Col } from 'antd'; // antd 에 Menu 사용

const AppLayout = ({ children }) => {
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item>
          <Link href="/">
            <a>노드버드</a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/profile">
            <a>프로필</a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Input.Search enterButton style={{ verticalAlign: 'middle' }} />
        </Menu.Item>
        <Menu.Item>
          <Link href="/signup">
            <a>회원가입</a>
          </Link>
        </Menu.Item>
      </Menu>
      <Row gutter={8}> {/*컬럼 사이의 간격 gutter*/}
        <Col xs={24} md={6}>
          {/*antd design 은 세로줄을 24 픽셀로 나눔*/}
          왼쪽 메뉴
        </Col>
        <Col xs={24} md={12}>
          {children} {/*여기는 AppLayout 의 children*/}
        </Col>
        <Col xs={24} md={6}>
          <a href="https://qhrdmaaksen.github.io/" target={'_blank'} rel="noreferrer" >vitamin blog</a>
        </Col>
      </Row>
    </div>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
