export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: '비타민',
      },
      content: '첫 번째 게시글 #해시태그 #익스프레스',
      Images: [{ src: '' }, { src: '' }, { src: '' }],
      Comments: [
        {
          User: {
            nickname: 'nero',
          },
          content: '개정판이 나왔네요!?',
        },
        {
          User: {
            nickname: 'hero',
          },
          content: '정말정말 신기하당!',
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false, // 포스트의 
}

const ADD_POST = 'ADD_POST';
export const addPost = {
  type: ADD_POST,
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return state
  }
}

export default reducer
