import React, {useState} from 'react';
import PropTypes from "prop-types"
import Slick from 'react-slick'
import styled, {createGlobalStyle} from 'styled-components'
import {CloseOutlined} from "@ant-design/icons";

const Overlay = styled.div`
	position: fixed;
	z-index: 5000;
	top: 0; // fixed 를 화면을 꽉채우려면 모두 0 주면된다.
	left: 0;
	right: 0;
	bottom: 0;
`

const Header = styled.header`
	header: 44px;
	background: white;
	position: relative;
	padding: 0;
	text-align: center;

	& h1 {
		margin: 0;
		font-size: 17px;
		color: #333;
		line-height: 44px;
	}
`

const CloseBtn = styled(CloseOutlined)`
	position: absolute;
	right: 0;
	top: 0;
	padding: 15px;
	line-height: 14px;
	cursor: pointer;
`

const SlickWrapper = styled.div`
	height: calc(100% - 44px);
	background: #090909;
`

const ImgWrapper = styled.div`
	padding: 32px;
	text-align: center;

	& img {
		margin: 0 auto;
		max-height: 750px;
	}
`

const Indicator = styled.div`
	text-align: center;

	& > div {
		width: 75px;
		height: 30px;
		line-height: 30px;
		border-radius: 15px;
		background: #313131;
		display: inline-block;
		text-align: center;
		color: white;
		font-size: 15px;
	}
`

const Global = createGlobalStyle`
	.slick-slide {
		display: inline-block;
	}
	.ant-card-cover {
		transform: none !important;
	}
`

const ImagesZoom = ({images, onClose}) => {
	const [currentSlide, setCurrentSlide] = useState(0)
	return (
			<Overlay>
				<Global/>
				<Header>
					<h1>상세 이미지</h1>
					<CloseBtn onClick={onClose}>X</CloseBtn>
				</Header>
				<SlickWrapper/>
				<div>
					<Slick
							initialSlike={0} // 첫번째를 어떤 이미지로 할지
							afterChange={(slide) => setCurrentSlide(slide)} // 슬라이드를 넘기면 번호가 저장
							infinite // 무한반복
							arrows={false} // 이미지 양 옆 클릭해서 넘기는 화살표 버튼 없앰
							slidesToShow={1} //한번에 하나씩 보여주며
							slidesToScroll={1} // 한번에 하나씩 넘긴다
					>
						{images.map((v) => (
								<ImgWrapper key={v.src}>
									<img src={v.src} alt={v.src}/>
								</ImgWrapper>
						))}
					</Slick>
				</div>
			</Overlay>
	)
}
ImagesZoom.propTypes = {
	images: PropTypes.arrayOf(PropTypes.object).isRequired,
	onClose: PropTypes.func.isRequired
}
export default ImagesZoom
