}

export default function* userSaga() {
	yield all([
			fork(watchLoadUser),
		fork(watchRemoveFollower),
		fork(watchLoadFollowers),
		fork(watchLoadFollowings),
		fork(watchChangeNickname),
		fork(watchLoadMyInfo),
		fork(watchFollow),
		fork(watchUnFollow),
		fork(watchLogIn),
		fork(watchLogOut),
		fork(watchSignUp),
	])
}
